import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm';
import reactDom from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm';
import { Card, Button } from 'https://cdn.jsdelivr.net/npm/antd@5.11.1/+esm';
import { Spin } from 'https://cdn.jsdelivr.net/npm/antd@5.11.1/+esm';

function truncateText(text, wordLimit) {
  const words = text.split(' ');
  if (words.length <= wordLimit) {
    return text;
  }
  return words.slice(0, wordLimit).join(' ') + '...';
}

function App() {
  const [users, setUsers] = React.useState(appsmith.model.users || []);
  const [loading, setLoading] = React.useState(true);

  React.useEffect(() => {
    const interval = setInterval(() => {
      const newUsers = appsmith.model.users;
      if (newUsers) {
        setUsers(newUsers);
        setLoading(false);
        clearInterval(interval);
      }
    }, 500);

    return () => clearInterval(interval);
  }, []);


  const handleProceed = async (user) => {
    appsmith.updateModel({ selectedUser: user });
    await appsmith.triggerEvent("proceed");
    //setUsers((prevUsers) => prevUsers.filter((u) => u !== user));
  };

  const handleReject = async (user) => {
    appsmith.updateModel({ selectedUser: user });
    await appsmith.triggerEvent("rejectUser");
    setUsers((prevUsers) => prevUsers.filter((u) => u !== user));
  };

  if (loading) {
    return (
      <div className="loading-container">
        <Spin size="large" tip="Loading users..." />
      </div>
    );
  }

  return (
    <div className="user-cards">
      {users.length === 0 ? (
        <p>No approval requests.</p>
      ) : (
        users.map((user, index) => (
          <Card
            key={index}
            className="user-card"
            style={{ backgroundColor: '#f0f2f5', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' }}
          >
            <div className="user-image">
              <img src={user.profile_image} alt={`${user.name}'s image`} />
            </div>
            <div className="user-info">
              <h2>{user.profile_name}</h2>
              <div className="user-details">
                <p>{user.phone_number}</p>
                <p>{user.email}</p>
                <p>{user.location}</p>
                <div className="separator"></div>
                <p>{truncateText(user.about, 10)}</p>
                <div className="separator"></div>
                <div className="button-container">
                  <Button type="primary" onClick={(e) => { e.stopPropagation(); handleProceed(user); }} >Proceed</Button>
                  <Button type="danger" onClick={(e) => { e.stopPropagation(); handleReject(user); }}>Reject</Button>
                </div>
              </div>
            </div>
          </Card>
        ))
      )}
    </div>
  );
}

appsmith.onReady(() => {
  reactDom.render(<App />, document.getElementById("root"));
});
