import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm';
import reactDom from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm';
import { Spin } from 'https://cdn.jsdelivr.net/npm/antd@5.11.1/+esm';
appsmith.onReady(function () {
  const organizationsList = document.getElementById('organizationsList');
  const loadingIndicator = document.getElementById('loadingIndicator');
  const searchInput = document.getElementById('searchInput');
  let organizations = []; // Global variable to store fetched organizations

  // Function to show Ant Design's Spin component while loading
  function showLoadingIndicator() {
    reactDom.render( /*#__PURE__*/React.createElement(Spin, {
      size: "large",
      tip: "Loading organizations..."
    }), loadingIndicator);
  }

  // Function to hide the loading indicator once the organizations are rendered
  function hideLoadingIndicator() {
    reactDom.render(null, loadingIndicator); // Clear the loading spinner
  }

  // Function to render the organization details
  function renderOrganizations(organizationsData) {
    let htmlContent = '';
    if (organizationsData && organizationsData.length > 0) {
      organizationsData.forEach(organization => {
        htmlContent += `
                     <div class="organization-card" data-organization='${JSON.stringify(organization).replace("'", "")}'>
                        <img class="organization-image" src="${organization.image ? organization.image : 'data:image/png;base64,'}" alt="${organization.name}" />
                        <div class="organization-info">
                            <div class="organization-row">
                                <div class="organization-name">${organization.name}</div>
																 <div class="organization-address">${organization.address || 'Address not provided'}</div>
                            </div>
                           <div class="organization-description">${organization.description || 'No description available'}</div>
                        </div>
                    </div>
                `;
      });
    } else {
      htmlContent = '<p>No organizations available.</p>';
    }
    organizationsList.innerHTML = htmlContent;

    // Hide the loading indicator once the organizations are rendered
    hideLoadingIndicator();

    // Attach click listeners to cards after rendering
    const organizationCards = document.querySelectorAll('.organization-card');
    organizationCards.forEach(card => {
      card.addEventListener('click', () => handleCardClick(card.dataset.organization));
    });
  }

  // Function to handle organization card click
  function handleCardClick(organizationData) {
    appsmith.updateModel({
      "organization": organizationData
    });
    appsmith.triggerEvent("onOrganizationClick");
    //alert(`Selected Organization: ${name}`);
  }

  // Function to handle search functionality
  function handleSearch() {
    const searchTerm = searchInput.value.toLowerCase();
    const filteredOrganizations = organizations.filter(organization => organization.name.toLowerCase().includes(searchTerm) || organization.address?.toLowerCase().includes(searchTerm) // Include address in the search safely
    );
    renderOrganizations(filteredOrganizations);
  }

  // Function to check for data and render organizations
  function checkForData() {
    const fetchedOrganizations = appsmith.model.organizations;
    if (fetchedOrganizations) {
      organizations = fetchedOrganizations; // Store the fetched organizations globally
      renderOrganizations(organizations);
    } else {
      // Retry after 500ms if the data is not available yet
      setTimeout(checkForData, 500);
    }
  }

  // Show the loading indicator while waiting for data
  showLoadingIndicator();

  // Start checking for data
  checkForData();

  // Attach event listener to the search input for live search filtering
  searchInput.addEventListener('input', handleSearch);
});

