// Global variable for institutions
let organizations = [];

// Function to create the institution card
function createOrganizationCard(organization) {
  return `
    <div class="organization-card" data-organization-name="${organization.name}">
      <img class="organization-image" src="${organization.image ? organization.image : 'data:image/png;base64,'}" alt="${organization.name}" />
      <div class="organization-info">
        <div class="organization-details">
          <div class="organization-name">${organization.name}</div>
          <div class="organization-description">${organization.description || 'No description available'}</div>
        </div>
        <div class="organization-address">${organization.address || 'Address not provided'}</div>
      </div>
    </div>
  `;
}

// Function to display the filtered institutions
function displayOrganizations(data) {
  const organizationsList = document.getElementById("organizationsList");
  organizationsList.innerHTML = data.map(organization => createOrganizationCard(organization)).join("");
}

// Function to handle card click
function handleCardClick(name) {
  alert(`Selected Organization: ${name}`);
}

// Function to handle search
function handleSearch() {
  const searchTerm = document.getElementById("searchInput").value.toLowerCase();
  const filteredOrganizations = organizations.filter(organization => organization.name.toLowerCase().includes(searchTerm) || organization.address?.toLowerCase().includes(searchTerm) // Include address in the search safely
  );
  displayOrganizations(filteredOrganizations);
}

// Initialization function to ensure everything is ready
appsmith.onReady(() => {
  const fetchedOrganizations = appsmith.model.organizations;
  organizations = fetchedOrganizations; // Store fetched institutions globally
  displayOrganizations(organizations);

  // Add search event listener after the DOM is ready
  const searchInput = document.getElementById("searchInput");
  searchInput.addEventListener("input", handleSearch);

  // Add click event listeners to institution cards
  const organizationCards = document.querySelectorAll('.organization-card');
  organizationCards.forEach(card => {
    card.addEventListener('click', () => handleCardClick(card.dataset.organizationName));
  });
});

