.work-experience-form {
  display: flex;
  flex-direction: column;
  gap: 10px;
  overflow: hidden;
}

.work-experience-field {
  display: flex;
  flex-direction: column;
  gap: 10px;
  position: relative;
  padding-right: 50px; /* Space for the remove button */
}

.work-experience-row {
  display: flex;
  gap: 10px;
}

.row1 input,
.row1 select {
  padding: 10px;
  border-radius: 5px;
  border: 1px solid #ddd;
}

.row1 input.organisation {
  flex: 2;
  font-size: 16px;
}

.row1 input.role {
  flex: 1.5;
  font-size: 14px;
}

.row2 input,
.row2 select {
  padding: 10px;
  border-radius: 5px;
  border: 1px solid #ddd;
}

.row2 input.start-date,
.row2 input.end-date {
  flex: 1;
  font-size: 14px;
}

.row2 input.skills {
  flex: 2;
  font-size: 14px;
}

.remove-work-experience-button {
  position: absolute;
  right: 10px; /* Space from the right edge */
  top: 50%; /* Center vertically */
  transform: translateY(-125%); /* Center vertically */
  width: 24px;
  height: 24px;
  background-color: #ff4d4f;
  color: white;
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 50%;
  cursor: pointer;
  transition: background-color 0.3s ease, transform 0.3s ease;
  z-index: 1; /* Ensure it's above other content */
}

.remove-work-experience-button:hover {
  background-color: #d9363e;
  transform: translateY(-125%) scale(1.1);
}

.add-work-experience-button {
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #1E1E1E;
  color: white;
  padding: 8px 12px; /* Smaller padding */
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.3s ease, transform 0.3s ease;
  width: 150px; /* Set a fixed width for the button */
  text-align: center; /* Center text inside the button */
  margin: 0 auto; /* Center the button horizontally */
}

.add-work-experience-button:hover {
  background-color: #1E1E1E;
  transform: scale(1.02); /* Slightly increase the size on hover */
}

.autocomplete-list {
  border: 1px solid #ddd;
  background-color: #fff;
  max-height: 97%;
  overflow-y: auto;
  position: absolute;
  top: 75%; /* Position dropdown directly below the text field */
  left: 0;
  width: 87%; /* Ensure dropdown matches the width of the text field */
  z-index: 1000;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
}

.autocomplete-list li {
  padding: 8px;
  cursor: pointer;
}

.autocomplete-list li:hover {
  background-color: #f0f0f0;
}

.workex-tabs {
  display: flex;
  justify-content: center;
  gap: 10px;
  margin-bottom: 10px;
}

.tab-button {
  padding: 8px 12px;
  cursor: pointer;
  background-color: #f0f0f0;
  border: none;
  border-radius: 5px;
  transition: background-color 0.3s ease;
}

.tab-button.active {
  background-color: #71717a;
  color: white;
}

.child-tab-button {
  padding: 4px 8px; /* Reduced padding for a smaller tab */
  cursor: pointer;
  background-color: #e0e0e0; /* Light gray for child tabs */
  border: none; /* No border to match parent tab style */
  border-radius: 5px; /* Same border radius */
  margin-left: 8px; /* Space between child tabs */
  transition: background-color 0.3s ease; /* Smooth transition */
  font-size: 12px; /* Smaller font size for child tabs */
  line-height: 1.2; /* Adjust line height for better alignment */
  max-width: fit-content; /* Ensures button width fits content */
}

.child-tab-button.active {
  background-color: #71717a; /* Same active color as parent tabs */
  color: white; /* White text for active child tab */
}

