			newWorkExperiences[index].institution_id = institutionId;
			newWorkExperiences[index].organization_id = organizationId? organizationId : '';
      setWorkExperiences(newWorkExperiences);
      appsmith.updateModel({ workexs: newWorkExperiences });
      setOrganizationSuggestions([]); // Clear suggestions after selection
      setFocusedIndex(null); // Clear the focused index after selection
    };

		const handleBlur = (index, event) => {
      setTimeout(() => {
        if (focusedIndex === index) {
          setOrganizationSuggestions([]); // Clear suggestions when focus is lost
        }
      }, 100); // Delay for allowing selection before blur happens
    };

    return (
      <div className="work-experience-form">
        <h3>Work Experience</h3>
        {workExperiences.map((experience, index) => (
          <div key={index} className="work-experience-field">
            <div
              className="remove-work-experience-button"
              onClick={() => removeWorkExperienceField(index)}
            >
              -
            </div>

						{/* Tab navigation for each organization */}
	<div className="workex-tabs">
		<button
	className={`tab-button ${!experience.isInstitute ? 'active' : ''}`}
								onClick={() => {handleTabChange(index, false), handleInputChange(index, event)}}
							>
								Company
							</button>
							<button
								className={`tab-button ${experience.isInstitute ? 'active' : ''}`}
								onClick={() => {handleTabChange(index, true), handleInputChange(index, event)}}
							>
								Institution
							</button>
							{/* Render additional tabs only if Institution is selected */}
									{experience.isInstitute && (
										<>
											<button
												className={`child-tab-button ${experience.isSchool ? 'active' : ''}`}
												onClick={() => handleInstitutionTypeChange(index, true)}
											>
												School
											</button>
											<button
												className={`child-tab-button ${!experience.isSchool ? 'active' : ''}`}
												onClick={() => handleInstitutionTypeChange(index, false)}
											>
												University
											</button>
										</>
									)}
						</div>


            <div className="row1 work-experience-row">
              <input
                type="text"
                name="organization_name"
                className="organisation"
                placeholder= {experience.isInstitute ? "Institution" : "Organization"}
                value={experience.organization_name}
                onChange={(event) => handleInputChange(index, event)}
                onFocus={() => setFocusedIndex(index)} // Set the focus when the user starts editing
								onBlur={(event) => handleBlur(index, event)}

              />
              {/* Organization Autocomplete Suggestions for the current focused field */}
              {organizationSuggestions.length > 0 && focusedIndex === index && (
                <ul className="autocomplete-list">
                  {organizationSuggestions.map((org) => (
                    <li
                      key={org.organization_id}
                      onClick={() => handleOrganizationSelect(index, org.name, org.institution_id, org.organization_id)}
                    >
                      {org.name}
                    </li>
									))}
                </ul>
              )}
              <input
                type="text"
                name="position"
                className="role"
                placeholder="Role"
                value={experience.position}
                onChange={(event) => handleInputChange(index, event)}
              />
            </div>

            <div className="currently-working">
              <input
                type="checkbox"
                name="currently_working"
                checked={experience.currently_working}
                onChange={(event) => handleInputChange(index, event)}
              />
              <label>Currently Working Here</label>
            </div>
          </div>
        ))}
        <div
          className="add-work-experience-button"
          onClick={addWorkExperienceField}
        >
          Add Another
        </div>
      </div>
    );
  }

  reactDom.render(<WorkExperienceForm />, document.getElementById('root'));
});
