appsmith.onReady(function () {
  // Check if there is an existing image in the model
  const existingImage = appsmith.model.image;

  // If an existing image is defined and not empty, display it
  if (existingImage && existingImage.trim() !== '') {
    const imgElement = document.getElementById('image-preview');
    imgElement.src = existingImage;
    imgElement.style.display = 'block'; // Show the image preview
  }

  // Event listener for file input
  document.getElementById('image-upload').addEventListener('change', function (event) {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = function (e) {
        const imgElement = document.getElementById('image-preview');
        imgElement.src = e.target.result;
        imgElement.style.display = 'block'; // Show the image preview

        // Update the model with the new image data
        appsmith.updateModel({
          image: e.target.result
        });
      };
      reader.readAsDataURL(file); // Read the file as a Data URL for image preview
    }
  });
});

