body, html {
  margin: 0;
  padding: 0;
  width: 100%;
  height: 100%;
  font-family: Arial, sans-serif;
}

.forums-container {
  padding: 20px;
  background: none; /* Remove background from here */
  min-height: 100vh;
  box-sizing: border-box;
  position: relative; /* Enable positioning for pseudo-element */
  overflow: hidden; /* Hide overflow to prevent blur from extending outside */
}

.forums-container::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: url("https://replicate.delivery/yhqm/GveeAfJpuDGdnJqA0grhfZeQWuedoFNTTAKvw7oF7MJkGb50E/out-0.webp") no-repeat center center/cover;
  filter: blur(8px); /* Adjust blur value here */
  z-index: -1; /* Ensure it's behind the content */
  transform: scale(1.1); /* Slight scaling to handle blur overflow */
}

.forums-heading {
  font-size: 36px;
  font-weight: bold;
  color: #000; /* Black color for heading */
  margin: 0; /* Spacing below the heading */
}

.search-box {
  width: 50%;
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 20px;
  font-size: 16px;
  outline: none;
}

.header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px; /* Spacing below header */
}

.forums-list {
  display: flex;
  flex-direction: column;
  gap: 10px; /* Spacing between forums */
}

.forum-item {
  background-color: rgba(255, 255, 255, 0.8);
  border-radius: 10px;
  padding: 15px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
  display: flex;
  align-items: center;
  position: relative;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.forum-item:hover {
  background-color: rgba(255, 255, 255, 0.9);
  transform: scale(1.02); /* Zoom effect on hover */
  box-shadow: 0 8px 20px rgba(0, 0, 0, 0.2);
  cursor: pointer;
}

.forum-item img {
  width: 100px; /* Adjust size as needed */
  height: auto;
  border-radius: 10px;
  flex-shrink: 0; /* Prevent image from shrinking */
  margin-right: 20px; /* Space between image and text */
}

.forum-details {
  display: flex;
  flex-direction: column;
  gap: 10px; /* Spacing between items */
  flex-grow: 1;
  text-align: justify;
  width: 10%;
  margin-right: 30px;
}

.forum-meta {
  display: flex;
  flex-direction: column;
  align-items: flex-end; /* Align to the right */
  gap: 5px; /* Spacing between location and date */
}

.forum-item h3 {
  font-size: 24px;
  margin: 0;
  color: #333;
}

.forum-item p {
  margin: 0;
  flex-grow: 1;
}

.forum-item .date {
  font-size: 14px;
  color: #666;
  flex-grow: 1;
}

.button {
  display: flex;
  align-items: center;
  font-size: 16px;
  color: #ffffff;
  background-color: #6c757d; /* Neutral gray */
  border: none;
  border-radius: 20px;
  padding: 10px 15px;
  --box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: background-color 0.3s, transform 0.2s;
  cursor: pointer;
  gap: 8px; /* Space between icon and text */
}

.button svg {
  fill: #ffffff; /* Icon color */
}

.button:hover {
  background-color: #5a6268; /* Darker neutral gray */
  transform: translateY(-3px);
}

.button:active {
  background-color: #545b62; /* Even darker neutral gray */
  transform: translateY(1px);
}

