appsmith.onReady(function () {
  const forums = appsmith.model.forums;
  const forumsList = document.getElementById('forumsList');
  function renderForums(filteredForums) {
    forumsList.innerHTML = ''; // Clear previous content
    filteredForums.forEach(forum => {
      const forumItem = document.createElement('div');
      forumItem.classList.add('forum-item');

      // Conditionally render image or adjust the layout if no image
      if (forum.forum_iamge) {
        forumItem.innerHTML = `
                    <img src="${forum.forum_image}" alt="${forum.title}">
                    <div class="forum-details">
                        <h3>${forum.title}</h3>
                        <p>${forum.description}</p>
                    </div>
                    <div class="forum-meta">
                        <div class="date">${forum.created_on}</div>
                    </div>
                `;
      } else {
        forumItem.innerHTML = `
                    <div class="forum-details no-image">
                        <h3>${forum.title}</h3>
                        <p>${forum.description}</p>
                    </div>
                    <div class="forum-meta">
                        <div class="date">${forum.created_on}</div>
                    </div>
                `;
      }
      forumItem.addEventListener('click', function () {
        appsmith.updateModel({
          selectedForum: JSON.stringify(forum)
        });
        appsmith.triggerEvent("onForumClick");
      });
      forumsList.appendChild(forumItem);
    });
  }
  document.getElementById('searchBox').addEventListener('input', function (e) {
    const query = e.target.value.toLowerCase();
    const filteredForums = forums.filter(forum => {
      return forum.title.toLowerCase().includes(query) || forum.created_on.toLowerCase().includes(query) || forum.description.toLowerCase().includes(query);
    });
    renderForums(filteredForums);
  });
  renderForums(forums); // Initial render

  const addEventButton = document.querySelector('.button');
  addEventButton.addEventListener('click', function () {
    appsmith.triggerEvent("onAddForumClick");
  });
});

