srcDoc.css:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: Arial, sans-serif;
  background-color: #f7f7f7;
}

.header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px;
  background-color: #66d2ff;
  color: #fff;
}

.header .logo {
  display: flex;
  align-items: center;
}

.header .logo img {
  width: 40px;
  margin-right: 10px;
}

.header h1 {
  font-size: 24px;
}

.header .profile-icon img {
  width: 40px;
}

.banner {
  text-align: center;
  padding: 50px 0;
  position: relative;
}

.banner-content h2 {
  font-size: 32px;
  color: #9c27b0;
  margin-bottom: 20px;
}

.banner-content p {
  color: #555;
  font-size: 18px;
  max-width: 600px;
  margin: 0 auto;
}

.main-sections {
  display: flex;
  flex-direction: column;
  gap: 20px;
  padding: 0; /* Removed side padding */
}

.section {
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  border-radius: 0; /* Removed border radius */
  height: 150px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  cursor: pointer;
  overflow: hidden;
  margin: 0;
  position: relative;
  color: #555; /* Ensures text color stays dark */
  padding: 40px 20px;
}

/* Horizontal fade to white on the left for Schools/Colleges and Events */
.section.schools::before, .section.events::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(to left, rgba(255, 255, 255, 0) 20%, white 90%);
  z-index: 1;
}

/* Horizontal fade to white on the right for Forums and Companies */
.section.forums::before, .section.companies::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(to right, rgba(255, 255, 255, 0) 20%, white 90%);
  z-index: 1;
}

.section h3, .section p {
  position: relative;
  z-index: 2;
}

/* Schools/Colleges and Events - Left Aligned */
.section.schools, .section.events {
  text-align: left;
}

/* Forums and Companies - Right Aligned */
.section.forums, .section.companies {
  text-align: right;
}

/* Background images with provided URL */
.section.schools {
  background-image: url("https://replicate.delivery/yhqm/HlcwHepJBDWQNinRVK0PUhByiEx2vork8KIF48lbFe0xyhWTA/out-0.webp");
}

.section.events {
  background-image: url("https://replicate.delivery/yhqm/Da0TjQEOkbqdNR4vCTKFCddfA7AFA7u85mRt6A1utlzB6QrJA/out-0.webp");
}

.section.forums {
  background-image: url("https://replicate.delivery/yhqm/4DVSni89Wg7tCdkSMNNTfXHjYEEt2Cw6uyB1DKa51mIrfcWTA/out-0.webp");
}

.section.companies {
  background-image: url("https://replicate.delivery/yhqm/IOlIfNYErIT3T6KbApmVPfhpl36i85D398zU755QL9ip3hWTA/out-0.webp");
}

.section h3 {
  font-size: 24px;
  margin-bottom: 15px;
  color: #9c27b0; /* Original color restored */
}

.section p {
  font-size: 16px;
  color: #555; /* Original color restored */
}

/* Hover effect */
.section.schools:hover h3, .section.schools:hover p,
.section.events:hover h3, .section.events:hover p {
  transform: translateX(5%) translateY(0%); /* Move text to the right */
}

.section.forums:hover h3, .section.forums:hover p,
.section.companies:hover h3, .section.companies:hover p {
  transform: translateX(-5%) translateY(0%); /* Move text to the left */
}

/* Reset styles */
.section h3, .section p {
  transition: transform 0.3s ease;
}

