* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: Arial, sans-serif;
  background-color: #f7f7f7;
}

.header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px;
  background-image: url("https://replicate.delivery/yhqm/rIL8dZv3lj6AFRvpUTPc6tp2ft7L5a2KDmardYfJOvZNmzWTA/out-0.webp");
  background-size: cover;
  background-position: center;
  position: relative;
  color: #fff;
}

.header::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(to bottom, rgba(255, 255, 255, 0.5), rgba(102, 210, 255, 0.3));
  z-index: 1;
}

.header .logo {
  display: flex;
  align-items: center;
  position: relative;
  z-index: 2;
}

.profile {
  display: flex;
  align-items: center;
  z-index: 1;
  cursor: pointer;
}

.profile-pic {
  width: 40px;
  height: 40px;
  margin-right: 0px;
}

.profile-name {
  font-size: 16px;
  color: #000;
  margin-right: 10px;
}

.header .logo img {
  width: 40px;
  margin-right: 10px;
}

.header h1 {
  font-size: 24px;
  color: #000;
}

.header .profile-icon img {
  width: 40px;
}

.banner {
  text-align: center;
  padding: 30px 0; /* Reduced padding */
  position: relative;
}

.banner-content h2 {
  font-size: 28px; /* Reduced font size */
  color: #9c27b0;
  margin-bottom: 15px; /* Reduced margin */
}

.banner-content p {
  color: #555;
  font-size: 16px; /* Reduced font size */
  max-width: 600px;
  margin: 0 auto;
}

.main-sections {
  display: flex;
  flex-direction: column;
  gap: 15px; /* Reduced gap between sections */
  padding: 0;
}

.section {
  background-size: cover;
  background-position: center;
  background-repeat: no-repeat;
  border-radius: 0;
  height: 120px; /* Reduced height */
  display: flex;
  flex-direction: column;
  justify-content: center;
  cursor: pointer;
  overflow: hidden;
  margin: 0;
  position: relative;
  color: #555;
  padding: 30px 20px; /* Reduced padding */
}

/* Horizontal fade to white on the left for Schools/Colleges and Events */
.section.schools::before, .section.events::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(to left, rgba(255, 255, 255, 0) 20%, white 90%);
  z-index: 1;
}

/* Horizontal fade to white on the right for Forums and Companies */
.section.forums::before, .section.companies::before {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(to right, rgba(255, 255, 255, 0) 20%, white 90%);
  z-index: 1;
}

.section h3, .section p {
  position: relative;
  z-index: 2;
}

/* Schools/Colleges and Events - Left Aligned */
.section.schools, .section.events {
  text-align: left;
}

/* Forums and Companies - Right Aligned */
.section.forums, .section.companies {
  text-align: right;
}

/* Background images with provided URL */
.section.schools {
  background-image: url("https://replicate.delivery/yhqm/HlcwHepJBDWQNinRVK0PUhByiEx2vork8KIF48lbFe0xyhWTA/out-0.webp");
}

.section.events {
  background-image: url("https://replicate.delivery/yhqm/Da0TjQEOkbqdNR4vCTKFCddfA7AFA7u85mRt6A1utlzB6QrJA/out-0.webp");
}

.section.forums {
  background-image: url("https://replicate.delivery/yhqm/4DVSni89Wg7tCdkSMNNTfXHjYEEt2Cw6uyB1DKa51mIrfcWTA/out-0.webp");
}

.section.companies {
  background-image: url("https://replicate.delivery/yhqm/IOlIfNYErIT3T6KbApmVPfhpl36i85D398zU755QL9ip3hWTA/out-0.webp");
}

.section h3 {
  font-size: 20px; /* Reduced font size */
  margin-bottom: 10px; /* Reduced margin */
  color: #9c27b0;
}

.section p {
  font-size: 14px; /* Reduced font size */
  color: #555;
}

/* Hover effect */
.section.schools:hover h3, .section.schools:hover p,
.section.events:hover h3, .section.events:hover p {
  transform: translateX(5%) translateY(0%);
}

.section.forums:hover h3, .section.forums:hover p,
.section.companies:hover h3, .section.companies:hover p {
  transform: translateX(-5%) translateY(0%);
}

/* Reset styles */
.section h3, .section p {
  transition: transform 0.3s ease;
}

body::before {
  content: "";
  position: absolute;
  width: 400px;
  height: 300px;
  background: radial-gradient(circle, rgba(156, 39, 176, 0.6), rgba(102, 210, 255, 0.3));
  border-radius: 50%;
  z-index: -1;
  opacity: 0.5;
}

body::before {
  top: 30px;
  left: 50%;
  transform: translateX(-50%) translateY(-50%);
}

