// Ensure the script is executed once Appsmith is ready
appsmith.onReady(function () {
  // Get the sections by their ID
  const schoolsSection = document.getElementById("schools");
  const forumsSection = document.getElementById("forums");
  const eventsSection = document.getElementById("events");
  const companiesSection = document.getElementById("companies");

  // Get the profile element by its class
  const profile = document.querySelector(".profile");

  // Add event listeners directly
  schoolsSection.addEventListener("click", function () {
    appsmith.triggerEvent("NavSchool");
  });
  forumsSection.addEventListener("click", function () {
    appsmith.triggerEvent("NavForum");
  });
  eventsSection.addEventListener("click", function () {
    appsmith.triggerEvent("NavEvent");
  });
  companiesSection.addEventListener("click", function () {
    appsmith.triggerEvent("NavCompany");
  });
  profile.addEventListener("click", function () {
    appsmith.triggerEvent("onProfileClick");
  });
});

