.header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
}

h2 {
  margin: 0;
  font-size: 30px;
  color: #231f20;
  font-family: inter;
}

h3 {
  margin-top: 20px;
  margin-bottom: 10px;
  color: #231f20;
  font-family: inter;
}

.search-bar {
  padding: 10px;
  font-size: 16px;
  width: 300px;
  border: 1px solid #ccc;
  border-radius: 4px;
}

.institutions-list {
  display: block; /* Full width for each card */
}

.institution-card {
  display: flex;
  flex-direction: row;
  align-items: center;
  background-color: #fff;
  border-radius: 8px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  padding: 15px;
  cursor: pointer;
  width: calc(100% - 40px); /* Full width minus any padding for margins */
  margin: 0 auto 20px; /* Center the card horizontally with automatic left and right margins */
}

.institution-card:hover {
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
}

.institution-image {
  width: 70px;
  height: auto;
  margin-right: 15px;
}

.institution-info {
  display: flex;
  flex-direction: column; /* Stack elements vertically */
  width: 100%; /* Full width */
}

.institution-name {
  font-size: 18px;
  font-weight: bold;
  color: #333;
  width: 100%;
}

.institution-description {
  font-size: 14px;
  color: #555;
  margin-top: 10px; /* Space above the description */
  text-align: justify; /* Justify the text */
  width: 100%; /* Full width for description */
  max-height: 3.6em; /* Adjust based on line-height */
  line-height: 1.2em; /* Space between lines */
  overflow: hidden; /* Hide overflow text */
  display: -webkit-box; /* Required for WebKit browsers */
  -webkit-line-clamp: 2; /* Limit to 3 lines */
  -webkit-box-orient: vertical; /* Necessary for line clamp to work */
  position: relative; /* Ensure position is relative */
}

.institution-address {
  font-size: 12px;
  color: #777;
  width: 50%; /* Ensure it takes full width */
  text-align: right; /* Align the address text */
  margin-top: 10px; /* Add some space above the address */
}

/* Section titles for new and updated institutions */
.section-title {
  font-size: 22px;
  font-weight: bold;
  margin-top: 40px;
  margin-bottom: 200px;
  padding: 10px;
  border-bottom: 2px solid #231f20;
  color: #231f20;
}

.section-title.new {
  color: #4caf50;
}

.section-title.updated {
  color: #ff9800; /* Orange color for updated institutions section */
}

/* No institutions message formatting */
.no-institutions {
  font-size: 16px;
  color: #888;
  margin-top: 10px;
  text-align: center;
}

.institution-row {
  display: flex; /* Use flex to align name and address */
  justify-content: space-between; /* Space between name and address */
  align-items: center; /* Center align vertically */
  width: 100%; /* Full width */
}

