import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm';
import reactDom from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm';
import { Spin } from 'https://cdn.jsdelivr.net/npm/antd@5.11.1/+esm';
appsmith.onReady(function () {
  const institutionsList = document.getElementById('institutionsList');
  const loadingIndicator = document.getElementById('loadingIndicator');
  const searchInput = document.getElementById('searchInput');
  let institutions = []; // Global variable to store fetched institutions

  // Function to show Ant Design's Spin component while loading
  function showLoadingIndicator() {
    reactDom.render( /*#__PURE__*/React.createElement(Spin, {
      size: "large",
      tip: "Loading institutions..."
    }), loadingIndicator);
  }

  // Function to hide the loading indicator once the institutions are rendered
  function hideLoadingIndicator() {
    reactDom.render(null, loadingIndicator); // Clear the loading spinner
  }

  // Function to render the institution details into two lists
  function renderInstitutions(institutionsData) {
    let newInstitutionsHtml = '';
    let updatedInstitutionsHtml = '';
    if (institutionsData && institutionsData.length > 0) {
      // Split the institutions based on their status
      institutionsData.forEach(institution => {
        const institutionCardHtml = `
                    <div class="institution-card" data-institution-data='${JSON.stringify(institution)}'>
                        <img class="institution-image" src="${institution.image ? institution.image : 'data:image/png;base64,'}" alt="${institution.name}" />
                        <div class="institution-info">
                            <div class="institution-row">
                                <div class="institution-name">${institution.name}</div>
																<div class="institution-address">${institution.address || 'Address not provided'}</div>

                            </div>
														<div class="institution-description">${institution.description || 'No description available'}</div>     
                        </div>
                    </div>
                `;

        // Check institution status and add to respective HTML variable
        if (institution.status === 'new') {
          newInstitutionsHtml += institutionCardHtml;
        } else if (institution.status === 'updated') {
          updatedInstitutionsHtml += institutionCardHtml;
        }
      });
    } else {
      newInstitutionsHtml = '<p>No institutions available.</p>';
    }

    // Combine both lists (new at the top, updated below)
    institutionsList.innerHTML = `
            <h3>New Institutions</h3>
            ${newInstitutionsHtml || '<p>No new institutions.</p>'}
            <h3>Updated Institutions</h3>
            ${updatedInstitutionsHtml || '<p>No updated institutions.</p>'}
        `;

    // Hide the loading indicator once the institutions are rendered
    hideLoadingIndicator();

    // Attach click listeners to cards after rendering
    const institutionCards = document.querySelectorAll('.institution-card');
    institutionCards.forEach(card => {
      card.addEventListener('click', () => handleCardClick(card.dataset.institutionData));
    });
  }

  // Function to handle institution card click
  function handleCardClick(institution) {
    appsmith.updateModel({
      "institution": institution
    });
    appsmith.triggerEvent("onInstitutionClick");
  }

  // Function to handle search functionality
  function handleSearch() {
    const searchTerm = searchInput.value.toLowerCase();
    const filteredInstitutions = institutions.filter(institution => institution.name.toLowerCase().includes(searchTerm) || institution.address?.toLowerCase().includes(searchTerm) // Include address in the search safely
    );
    renderInstitutions(filteredInstitutions);
  }

  // Function to check for data and render institutions
  function checkForData() {
    const fetchedInstitutions = appsmith.model.institutions;
    if (fetchedInstitutions) {
      institutions = fetchedInstitutions; // Store the fetched institutions globally
      renderInstitutions(institutions);
    } else {
      // Retry after 500ms if the data is not available yet
      setTimeout(checkForData, 500);
    }
  }

  // Show the loading indicator while waiting for data
  showLoadingIndicator();

  // Start checking for data
  checkForData();

  // Attach event listener to the search input for live search filtering
  searchInput.addEventListener('input', handleSearch);
});

