/* General Page Styles */
body, html {
  margin: 0;
  padding: 0;
  font-family: "Arial", sans-serif;
  background: url("background.jpg") no-repeat center center fixed;
  background-size: cover;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
}

.container {
  position: relative;
  text-align: center;
  width: 100vw;
  height: 100vh;
}

/* Header and Logo */
header {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  padding: 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  z-index: 2;
}

.logo {
  font-size: 2rem;
  font-weight: bold;
  color: white;
  display: flex;
  align-items: center;
}

.logo img {
  width: 50px;
  margin-right: 10px;
}

/* Register Button */
.register-btn {
  background-color: white;
  border: 1px solid #ccc;
  padding: 10px 20px;
  border-radius: 5px;
  text-decoration: none;
  color: black;
  font-weight: bold;
  transition: background-color 0.3s ease;
}

.register-btn:hover {
  background-color: #f0f0f0;
}

/* Hero Text */
.hero-text {
  font-size: 2.5rem;
  color: white;
  font-weight: bold;
  position: absolute;
  top: 20%;
  left: 50%;
  transform: translate(-50%, -50%);
  text-shadow: 2px 2px 5px rgba(0, 0, 0, 0.5);
}

.hero-text span {
  color: #8A2BE2; /* Highlighted purple */
}

/* Login Form Container */
.form-container {
  position: absolute;
  bottom: 10%;
  left: 50%;
  transform: translateX(-50%);
  background-color: rgba(255, 255, 255, 0.85);
  padding: 30px;
  border-radius: 20px;
  width: 400px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3);
}

.form-container input {
  width: 100%;
  padding: 10px;
  margin-bottom: 20px;
  border-radius: 5px;
  border: 1px solid #ccc;
  font-size: 1rem;
}

.form-container a {
  text-align: right;
  display: block;
  color: #8A2BE2;
  text-decoration: none;
  margin-bottom: 20px;
}

.form-container button {
  background-color: #8A2BE2;
  color: white;
  border: none;
  padding: 10px;
  border-radius: 5px;
  font-size: 1.2rem;
  cursor: pointer;
  width: 100%;
}

.form-container button:hover {
  background-color: #5a189a;
}

/* Additional Elements */
.decorative-element {
  position: absolute;
  width: 100px;
  height: 100px;
  background-color: #8A2BE2;
  transform: rotate(45deg);
  z-index: 1;
}

.decorative-element.top-right {
  top: 10%;
  right: 5%;
}

.decorative-element.bottom-left {
  bottom: 10%;
  left: 10%;
}

