document.addEventListener('DOMContentLoaded', function () {
  // Create main container
  const container = document.createElement('div');
  container.className = 'container';

  // Create header section
  const header = document.createElement('header');
  const logo = document.createElement('div');
  logo.className = 'logo';
  const logoImg = document.createElement('img');
  logoImg.src = 'logo.png'; // Change to your logo path
  const logoText = document.createElement('span');
  logoText.innerText = 'Community Portal';
  logo.appendChild(logoImg);
  logo.appendChild(logoText);
  header.appendChild(logo);

  // Register button
  const registerBtn = document.createElement('a');
  registerBtn.className = 'register-btn';
  registerBtn.innerText = 'Register';
  registerBtn.href = '#';
  header.appendChild(registerBtn);

  // Hero text
  const heroText = document.createElement('div');
  heroText.className = 'hero-text';
  heroText.innerHTML = 'Our <span>Community</span>, Our <span>Connections</span>,<br>Thriving <span>Together</span>.';

  // Login form container
  const formContainer = document.createElement('div');
  formContainer.className = 'form-container';
  const form = document.createElement('form');

  // Email input
  const emailInput = document.createElement('input');
  emailInput.type = 'email';
  emailInput.placeholder = 'Enter registered email';
  form.appendChild(emailInput);

  // Password input
  const passwordInput = document.createElement('input');
  passwordInput.type = 'password';
  passwordInput.placeholder = 'Enter password';
  form.appendChild(passwordInput);

  // Forgot password link
  const forgotPassword = document.createElement('a');
  forgotPassword.href = '#';
  forgotPassword.innerText = 'Forgot Password?';
  form.appendChild(forgotPassword);

  // Sign-in button
  const signInBtn = document.createElement('button');
  signInBtn.type = 'submit';
  signInBtn.innerText = 'Sign In';
  form.appendChild(signInBtn);
  formContainer.appendChild(form);

  // Append all elements to the container
  container.appendChild(header);
  container.appendChild(heroText);
  container.appendChild(formContainer);

  // Add decorative elements
  const decorTopRight = document.createElement('div');
  decorTopRight.className = 'decorative-element top-right';
  const decorBottomLeft = document.createElement('div');
  decorBottomLeft.className = 'decorative-element bottom-left';
  container.appendChild(decorTopRight);
  container.appendChild(decorBottomLeft);

  // Append container to the body
  document.body.appendChild(container);
});

