body, html {
  margin: 0;
  padding: 0;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  font-family: Arial, sans-serif;
  background: linear-gradient(to bottom, rgba(255, 255, 255, 0.4), rgba(255, 255, 255, 0)), url("https://replicate.delivery/yhqm/rIL8dZv3lj6AFRvpUTPc6tp2ft7L5a2KDmardYfJOvZNmzWTA/out-0.webp") no-repeat center center/cover;
  overflow: hidden;
}

.header {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  background-color: rgba(255, 255, 255, 0.8);
  padding: 10px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
  z-index: 1000;
}

.header .logo {
  display: flex;
  align-items: center;
}

.header .logo img {
  width: 50px;
  height: 50px;
  margin-right: 15px;
}

.header .logo h2 {
  font-size: 22px;
  font-weight: bold;
  color: #000;
}

.main-content {
  position: relative;
  top: 10%;
  z-index: 1;
  display: flex;
  flex-direction: column;
  align-items: flex-start; /* Align items to the start of the container */
}

.tagline {
  font-size: 30px;
  font-weight: bold;
  color: #4A90E2;
  display: inline-block;
  padding: 10px 20px;
  border-radius: 10px;
  margin-bottom: 20px; /* Space between tagline and login box */
}

.tagline strong {
  color: #8A2BE2;
}

.login-box {
  --background: rgba(255, 255, 255, 0.9);
  background: rgba(173, 216, 230, 0.8);
  border-radius: 15px;
  padding: 30px;
  margin: auto;
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.5);
  transform: translate(0%, 50%);
}

.input-field {
  width: 94.5%;
  padding: 12px;
  margin-top: 10px;
  margin-bottom: 10px;
  border-radius: 5px;
  border: 1px solid #ccc;
  font-size: 16px;
}

.button {
  padding: 10px 20px;
  border: none;
  border-radius: 5px;
  background-color: #8a2be2;
  color: white;
  font-size: 18px;
  cursor: pointer;
  transition: background-color 0.3s ease;
  margin-right: 10px;
  margin-top: 10px;
}

.button:hover {
  background-color: #5e1c9a;
}

.link {
  color: #8a2be2;
  text-decoration: none;
  font-size: 14px;
  aligh: right;
  margin-left: 20px;
}

.link:hover {
  text-decoration: underline;
}

.signup {
  position: absolute;
  top: 5%;
  right: 20px;
  display: flex;
  align-items: center;
  font-size: 14px;
}

.signup button {
  margin-left: 10px;
  padding: 8px 15px;
  background-color: #D3D3D3;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  font-size: 16px;
}

.signup button:hover {
  background-color: #C0C0C0;
}

.shape1, .shape2 {
  position: absolute;
  z-index: -1;
}

.shape1 {
  background-color: rgba(173, 216, 230, 0.8); /* Light blue square */
  width: 600px;
  height: 180px;
  top: 5%; /* Adjust position */
  left: 5%; /* Adjust position */
  transform: translate(-20%, -40%) rotate(-15deg);
}

.shape2 {
  width: 0;
  height: 0;
  border-left: 130px solid transparent; /* Half of shape1's width */
  border-right: 100px solid transparent; /* Half of shape1's width */
  border-bottom: 500px solid #ffffff; /* Match shape1's height */
  position: absolute;
  top: 50%; /* Adjust position */
  right: 58%; /* Adjust position */
  transform: translate(50%, -50%) rotate(-95deg); /* Tilt shape */
  z-index: -1; /* Ensure it is behind other content */
}

