appsmith.onReady(function () {
  const experienceDetails = document.getElementById('experienceDetails');
  function renderWorkExperience(experience) {
    let htmlContent = '';
    if (experience && experience.length > 0) {
      experience.forEach(exp => {
        htmlContent += `
                    <div class="card">
                        <div class="card-content">
                            <div class="card-row">
                                <div class="card-column organization">${exp.organization_name}</div>
                                <div class="card-column role">${exp.position}</div>
                                <div class="card-column date">${exp.start_date} - ${exp.end_date || 'Currently Working'}</div>
                            </div>
                            <div class="card-row">
                                <div class="card-column skills">${exp.skills}</div>
																<div class="card-column date">${exp.type}</div>
                            </div>
                        </div>
                    </div>
                `;
      });
    } else {
      htmlContent = '<p>No work experience details available.</p>';
    }
    experienceDetails.innerHTML = htmlContent;
  }
  function checkForWorkExperienceData() {
    const experience = appsmith.model.workex;
    if (experience) {
      renderWorkExperience(experience);
    } else {
      // Retry after 500ms if the data is not available yet
      setTimeout(checkForWorkExperienceData, 500);
    }
  }
  checkForWorkExperienceData();
});

