appsmith.onReady(function () {
  const experienceDetails = document.getElementById('experienceDetails');
  const loadingIndicator = document.getElementById('loadingIndicator');
  function showLoading() {
    loadingIndicator.style.display = 'block'; // Show the loading indicator
  }
  function hideLoading() {
    loadingIndicator.style.display = 'none'; // Hide the loading indicator
  }
  function renderWorkExperience(experience) {
    let htmlContent = '';
    if (experience && experience.length > 0) {
      experience.forEach((exp, index) => {
        htmlContent += `
                    <div class="card" id="exp-card-${index}">
                        <div class="card-content">
                            <div class="card-row">
                                <div class="card-column organization">${exp.organization_name}</div>
                                <div class="card-column role">${exp.position}</div>
                                <div class="card-column date">${exp.currently_working ? 'Currently Working' : 'Ended'}</div>
                            </div>
                            <div class="card-row">
                            </div>
                        </div>
                    </div>
                `;
      });
    } else {
      htmlContent = '<p>No work experience details available.</p>';
    }
    experienceDetails.innerHTML = htmlContent;

    // Add click event listener to each work experience card
    experience.forEach((exp, index) => {
      const card = document.getElementById(`exp-card-${index}`);
      if (card) {
        card.addEventListener('click', function () {
          appsmith.updateModel({
            "selectedWorkex": JSON.stringify(exp.organization[0])
          });
          appsmith.triggerEvent("workexClicked");
        });
      }
    });
    hideLoading();
  }
  function checkForWorkExperienceData() {
    showLoading();
    const experience = appsmith.model.workex;
    if (experience) {
      renderWorkExperience(experience);
    } else {
      // Retry after 500ms if the data is not available yet
      setTimeout(checkForWorkExperienceData, 500);
    }
  }
  checkForWorkExperienceData();
});

