.user-cards {
  display: flex;
  flex-wrap: wrap;
  gap: 20px;
  padding: 20px;
}

.user-card {
  width: 200px;
  text-align: center;
  border-radius: 10px;
  overflow: hidden;
  transition: transform 0.3s, box-shadow 0.3s;
}

.user-card:hover {
  transform: translateY(-10px);
  box-shadow: 0 8px 16px rgba(0, 0, 0, 0.2);
}

.user-image img {
  width: 100%;
  height: auto;
  border-bottom: 1px solid #ddd;
}

.user-info {
  padding: 10px;
}

