import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm';
import reactDom from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm';
import { Card } from 'https://cdn.jsdelivr.net/npm/antd@5.11.1/+esm';

function App() {
  const users = appsmith.model.Users;

  return (
    <div className="user-cards">
      {users.map((userObj, index) => {
        const userId = Object.keys(userObj)[0];
        const user = userObj[userId];
        return (
          <Card key={index} className="user-card" style={{ backgroundColor: '#f0f2f5', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)' }}>
            <div className="user-image">
              <img src={user.image} alt={`${user.name}'s image`} />
            </div>
            <div className="user-info">
              <h2>{user.name}</h2>
            </div>
          </Card>
        );
      })}
    </div>
  );
}

appsmith.onReady(() => {
  /*
   * This handler function will get called when parent application is ready.
   * Initialize your component here
   * more info - https://docs.appsmith.com/reference/widgets/custom#onready
   */
  reactDom.render(<App />, document.getElementById("root"));
});
