body, html {
  margin: 0;
  padding: 0;
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  font-family: Arial, sans-serif;
  background: linear-gradient(to bottom, rgba(255, 255, 255, 0.4), rgba(255, 255, 255, 0)), url("https://replicate.delivery/yhqm/rIL8dZv3lj6AFRvpUTPc6tp2ft7L5a2KDmardYfJOvZNmzWTA/out-0.webp") no-repeat center center/cover;
  overflow: scroll;
  scrollbar-width: none;
}

.header {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  background-color: rgba(255, 255, 255, 0.8);
  padding: 10px 20px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
  z-index: 1000;
}

.header .logo {
  display: flex;
  align-items: center;
}

.header .logo img {
  width: 50px;
  height: 50px;
  margin-right: 15px;
}

.header .logo h2 {
  font-size: 22px;
  font-weight: bold;
  color: #000;
}

.main-content {
  position: relative;
  top: 10%;
  z-index: 1;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
}

.tagline {
  font-size: 30px;
  font-weight: bold;
  color: #4A90E2;
  display: inline-block;
  padding: 10px 20px;
  border-radius: 10px;
  margin-bottom: 20px; /* Space between tagline and login box */
}

.tagline strong {
  color: #8A2BE2;
}

.login-box {
  --background: rgba(255, 255, 255, 0.9);
  background: rgba(173, 216, 230, 0.8);
  border-radius: 15px;
  padding: 30px;
  margin: auto;
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.5);
  transform: translate(0%, 5%);
  width: 90%;
  position: relative;
  z-index: -10;
}

.upload-image {
  display: flex;
  justify-content: center;
  align-items: center;
  position: relative;
  padding-bottom: 15px;
}

.image-preview {
  width: 100px; /* Set width and height of the circle */
  height: 100px;
  border-radius: 50%; /* Make it circular */
  overflow: hidden; /* Hide overflowed content */
  border: 2px solid #8A2BE2; /* Optional: Border color */
  background-color: #f7f7f7; /* Optional: Background color */
  position: relative; /* Position context for the icon */
}

#uploadedImage {
  width: 100%; /* Fit image to container */
  height: 100%; /* Fit image to container */
  object-fit: cover; /* Maintain aspect ratio */
  display: block; /* Ensure the image is displayed properly */
}

.upload-label {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  font-size: 24px;
  color: #4A90E2;
  cursor: pointer;
  z-index: 10; /* Ensure it is on top of the image */
}

/* Hide the default file input button */
#imageUpload {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  opacity: 0;
  cursor: pointer;
}

.form-field {
  display: flex;
  justify-content: space-between;
  margin-bottom: 15px;
}

.form-field input,
.form-field textarea {
  width: 46%;
  padding: 12px;
  border-radius: 5px;
  border: 1px solid #ccc;
  font-size: 16px;
}

.form-field select {
  width: 48.5%;
  padding: 12px;
  border-radius: 5px;
  border: 1px solid #ccc;
  font-size: 16px;
}

.form-field textarea {
  width: 100%;
  height: 50px;
  resize: auto;
}

/* Readonly input field */
.input-field[readonly] {
  background-color: #f0f0f0; /* Light grey background */
  cursor: not-allowed; /* Show that the field is not editable */
}

.button-link-wrapper {
  display: flex;
  justify-content: center;
}

.button {
  padding: 10px 50px;
  border: none;
  border-radius: 5px;
  background-color: #8a2be2;
  color: white;
  font-size: 18px;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.button:hover {
  background-color: #5e1c9a;
}

.signin {
  position: absolute;
  top: 5%;
  right: 20px;
  display: flex;
  align-items: center;
  font-size: 14px;
}

.signin button {
  margin-left: 10px;
  padding: 8px 15px;
  background-color: #D3D3D3;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  font-size: 16px;
}

.signin button:hover {
  background-color: #C0C0C0;
}

.shape1, .shape2 {
  position: absolute;
  z-index: -1;
}

.shape1 {
  background-color: rgba(173, 216, 230, 0.8); /* Light blue square */
  width: 600px;
  height: 180px;
  top: 5%; /* Adjust position */
  left: 5%; /* Adjust position */
  transform: translate(-20%, -40%) rotate(-15deg);
}

.shape2 {
  width: 0;
  height: 0;
  border-left: 130px solid transparent; /* Half of shape1's width */
  border-right: 100px solid transparent; /* Half of shape1's width */
  border-bottom: 500px solid #ffffff; /* Match shape1's height */
  position: absolute;
  top: 50%; /* Adjust position */
  right: 58%; /* Adjust position */
  transform: translate(50%, -50%) rotate(-95deg); /* Tilt shape */
  z-index: -1; /* Ensure it is behind other content */
}

.education-section, .work-experience-section {
  margin-bottom: 20px;
}

.education-section h3, .work-experience-section h3 {
  font-size: 22px;
  margin-bottom: 10px;
}

.education-section .button,
.work-experience-section .button {
  background-color: #add8e6; /* Light blue color */
  color: #000;
  padding: 8px 20px;
  font-size: 16px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.education-section .button:hover,
.work-experience-section .button:hover {
  background-color: #87ceeb; /* Slightly darker blue on hover */
}

.entry-container {
  margin-bottom: 5px;
  padding: 12px;
  border: 1px solid #ccc;
  border-radius: 5px;
  background: #f9f9f9;
}

.entry-row {
  display: flex;
  justify-content: space-between;
  gap: 10px;
  margin-bottom: 10px;
}

.entry-row .input-field {
  width: 46%;
  padding: 12px;
  border-radius: 5px;
  border: 1px solid #ccc;
  font-size: 16px;
}

.entry-row .input-field.full-width {
  width: 100%;
}

.remove-entry {
  background-color: #e74c3c;
  color: white;
  padding: 10px 15px;
}

.remove-entry:hover {
  background-color: #c0392b;
