window.displayImage = function (event) {
  const input = event.target;
  const file = input.files[0];
  const imagePreview = document.getElementById('uploadedImage');
  if (file) {
    const reader = new FileReader();
    reader.onload = function (e) {
      imagePreview.src = e.target.result;
      imagePreview.style.display = 'block'; // Show the image preview
    };
    reader.readAsDataURL(file);
  } else {
    imagePreview.src = '#';
    imagePreview.style.display = 'none'; // Hide the image preview if no file is selected
  }
};
function addEducationEntry() {
  const container = document.getElementById('educationContainer');
  const entryCount = container.children.length;
  const entryHtml = `
        <div class="entry-container">
            <div class="entry-row">
                <input type="text" class="input-field" placeholder="Institution" name="institution-${entryCount}">
                <input type="text" class="input-field" placeholder="Specialization" name="specialization-${entryCount}">
            </div>
            <div class="entry-row">
                <input type="date" class="input-field" name="startDateEdu-${entryCount}">
                <input type="date" class="input-field" name="endDateEdu-${entryCount}">
                <input type="text" class="input-field" placeholder="Marks (%) or CGPA" name="marks-${entryCount}">
            </div>
            <button type="button" class="button remove-entry" onclick="removeEntry(this)">Remove</button>
        </div>
    `;
  container.insertAdjacentHTML('beforeend', entryHtml);
}
function addWorkExperienceEntry() {
  const container = document.getElementById('workExperienceContainer');
  const entryCount = container.children.length;
  const entryHtml = `
        <div class="entry-container">
            <div class="entry-row">
                <input type="text" class="input-field" placeholder="Organisation" name="organisation-${entryCount}">
                <input type="text" class="input-field" placeholder="Role" name="roleWork-${entryCount}">
            </div>
            <div class="entry-row">
                <input type="date" class="input-field" name="startDateWork-${entryCount}">
                <input type="date" class="input-field" name="endDateWork-${entryCount}">
                <select class="input-field" name="typeWork-${entryCount}">
                    <option value="">Type of Work</option>
                    <option value="remote">Remote</option>
                    <option value="onsite">Onsite</option>
                    <option value="hybrid">Hybrid</option>
                </select>
            </div>
						<div class="entry-row">
            <input type="text" class="input-field full-width" placeholder="Skills" name="skills-${entryCount}">
						</div>
            <button type="button" class="button remove-entry" onclick="removeEntry(this)">Remove</button>
        </div>
    `;
  container.insertAdjacentHTML('beforeend', entryHtml);
}
function handleRemoveEntry(event) {
  if (event.target.classList.contains('remove-entry')) {
    const entryContainer = event.target.closest('.entry-container');
    if (entryContainer) {
      entryContainer.remove();
    }
  }
}
appsmith.onReady(function () {
  document.getElementById('name').value = appsmith.model.user.name;
  document.getElementById('phone').value = appsmith.model.user.phoneno;
  document.getElementById('email').value = appsmith.model.user.email;
  document.getElementById('role').value = appsmith.model.user.role;
  document.getElementById('location').value = appsmith.model.user.location;
  document.getElementById('communityID').value = appsmith.model.user.commuity_id;
  document.getElementById('about').value = appsmith.model.user.about;
  document.getElementById('interests').value = appsmith.model.user.interest;
  document.getElementById('uploadedImage').src = appsmith.model.user.image;
  document.addEventListener('click', handleRemoveEntry);
  document.getElementById('addEducation').addEventListener('click', addEducationEntry);
  document.getElementById('addWorkExperience').addEventListener('click', addWorkExperienceEntry);
  document.getElementById('registerButton').addEventListener('click', function () {
    const name = document.getElementById('name').value;
    const phone = document.getElementById('phone').value;
    const email = document.getElementById('email').value;
    const role = document.getElementById('role').value;
    const location = document.getElementById('location').value;
    const communityID = document.getElementById('communityID').value;
    const about = document.getElementById('about').value;
    const interests = document.getElementById('interests').value;
    const imageUrl = document.getElementById('uploadedImage').src;
    const educationEntries = Array.from(document.querySelectorAll('#educationContainer .entry-container')).map(entry => ({
      institution: entry.querySelector('input[name^="institution"]').value,
      specialization: entry.querySelector('input[name^="specialization"]').value,
      startDate: entry.querySelector('input[name^="startDateEdu"]').value,
      endDate: entry.querySelector('input[name^="endDateEdu"]').value,
      marks: entry.querySelector('input[name^="marks"]').value
    }));
    const workExperienceEntries = Array.from(document.querySelectorAll('#workExperienceContainer .entry-container')).map(entry => ({
      organisation: entry.querySelector('input[name^="organisation"]').value,
      role: entry.querySelector('input[name^="roleWork"]').value,
      skills: entry.querySelector('input[name^="skills"]').value,
      startDate: entry.querySelector('input[name^="startDateWork"]').value,
      endDate: entry.querySelector('input[name^="endDateWork"]').value,
      type: entry.querySelector('select[name^="typeWork"]').value
    }));
    const userDetails = {
      name: name,
      phone: phone,
      email: email,
      role: role,
      location: location,
      communityID: communityID,
      about: about,
      interests: interests,
      imageUrl: imageUrl,
      education: educationEntries,
      workExperience: workExperienceEntries
    };
    appsmith.updateModel({
      "userDetails": userDetails
    });
    appsmith.triggerEvent("onRegisterClick");
  });
  document.getElementById('loginButton').addEventListener('click', function () {
    appsmith.triggerEvent("onSignInClick");
  });
});

