            marks: entry.querySelector('input[name^="marks"]').value
        }));

        const workExperienceEntries = Array.from(document.querySelectorAll('#workExperienceContainer .entry-container')).map(entry => ({
            organisation: entry.querySelector('input[name^="organisation"]').value,
            role: entry.querySelector('input[name^="roleWork"]').value,
            skills: entry.querySelector('input[name^="skills"]').value,
            startDate: entry.querySelector('input[name^="startDateWork"]').value,
            endDate: entry.querySelector('input[name^="endDateWork"]').value,
            type: entry.querySelector('select[name^="typeWork"]').value
        }));

        const userDetails = {
            name: name,
            phone: phone,
            email: email,
            role: role,
            location: location,
            communityID: communityID,
            about: about,
						interests: interests,
            imageUrl: imageUrl,
            education: educationEntries,
            workExperience: workExperienceEntries
        };

        appsmith.updateModel({"userDetails" : userDetails});
        appsmith.triggerEvent("onRegisterClick");
    });

    document.getElementById('loginButton').addEventListener('click', function() {
        appsmith.triggerEvent("onSignInClick");
    });
});
