appsmith.onReady(function () {
  const educationDetails = document.getElementById('educationDetails');
  const loadingIndicator = document.getElementById('loadingIndicator');
  function showLoading() {
    loadingIndicator.style.display = 'block'; // Show the loading indicator
  }
  function hideLoading() {
    loadingIndicator.style.display = 'none'; // Hide the loading indicator
  }
  function renderEducation(education) {
    let htmlContent = '';
    if (education && education.length > 0) {
      education.forEach((edu, index) => {
        htmlContent += `
                    <div class="card" id="card-${index}">
                        <div class="card-content">
                            <div class="card-row">
                                <div class="card-column institution">${edu.institution_name}</div>
                                <div class="card-column degree">${edu.degree}</div>
                                <div class="card-column date">${edu.currently_pursuing ? 'Currently Pursuing' : 'Ended'}</div>
                            </div>
                            <div class="card-row">
                                <div class="card-column">${edu.specialization}</div>
                            </div>
                        </div>
                    </div>
                `;
      });
    } else {
      htmlContent = '<p>No education details available.</p>';
    }
    educationDetails.innerHTML = htmlContent;

    // Add click event listener to each card
    education.forEach((edu, index) => {
      const card = document.getElementById(`card-${index}`);
      if (card) {
        card.addEventListener('click', function () {
          appsmith.updateModel({
            'clickedEducation': JSON.stringify(edu.institution[0])
          });
          appsmith.triggerEvent("educationClicked");
        });
      }
    });
    hideLoading();
  }
  function checkForData() {
    showLoading();
    const education = appsmith.model.education;
    if (education) {
      renderEducation(education);
    } else {
      // Retry after 500ms if the data is not available yet
      setTimeout(checkForData, 500);
    }
  }
  checkForData();
});

