appsmith.onReady(function () {
  const educationDetails = document.getElementById('educationDetails');
  function renderEducation(education) {
    let htmlContent = '';
    if (education && education.length > 0) {
      education.forEach(edu => {
        htmlContent += `
                    <div class="card">
                        <div class="card-content">
                            <div class="card-row">
                                <div class="card-column institution">${edu.school}</div>
                                <div class="card-column degree">${edu.degree}</div>
                                <div class="card-column date">${edu.startdate} - ${edu.enddate || 'Present'}</div>
                            </div>
                            <div class="card-row">
                                <div class="card-column">${edu.specialization}</div>
                                <div class="card-column marks">${edu.marks} %/CGPA</div>
                            </div>
                        </div>
                    </div>
                `;
      });
    } else {
      htmlContent = '<p>No education details available.</p>';
    }
    educationDetails.innerHTML = htmlContent;
  }
  function checkForData() {
    const education = appsmith.model.education;
    if (education) {
      renderEducation(education);
    } else {
      // Retry after 500ms if the data is not available yet
      setTimeout(checkForData, 500);
    }
  }
  checkForData();
});

