body, html {
  margin: 0;
  padding: 0;
  width: 100%;
  height: 100%;
  font-family: Arial, sans-serif;
}

.profile-container {
  background: url("https://replicate.delivery/yhqm/GveeAfJpuDGdnJqA0grhfZeQWuedoFNTTAKvw7oF7MJkGb50E/out-0.webp") no-repeat center center/cover;
  min-height: 100vh;
  box-sizing: border-box;
}

.profile-details {
  background-color: rgba(255, 255, 255, 0.8);
  padding: 20px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
  display: flex;
  flex-direction: column;
  gap: 10px;
  position: relative; /* Added for positioning the icon */
}

.profile-item {
  display: flex;
  align-items: center;
  margin-bottom: 15px;
}

.profile-item img {
  width: 100px;
  height: 100px;
  border-radius: 50%;
  margin-right: 20px;
}

.profile-info {
  display: flex;
  flex-direction: column;
  gap: 5px;
}

.profile-info h2 {
  font-size: 24px;
  margin: 0;
  color: #333;
}

.profile-info p {
  margin: 0;
  font-size: 16px;
  color: #666;
}

.edit-icon {
  position: absolute;
  top: 20px;
  right: 20px;
  cursor: pointer;
  fill: #9c27b0; /* Purple color */
  transition: fill 0.3s;
}

.edit-icon:hover {
  fill: #7b1fa2; /* Darker purple on hover */
}

.card {
  background-color: #fff;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  margin-bottom: 0px;
  padding: 10px;
  transition: box-shadow 0.3s ease;
}

.card-content {
  display: flex;
  flex-direction: column;
}

.card-row {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 0px;
}

.card-column.role {
  text-align: center; /* Center role text horizontally */
}

.card-column {
  flex: 1;
  padding: 5px;
  font-size: 14px;
  color: #666;
}

.card-column.institution {
  font-weight: bold;
  font-size: 18px;
  color: #333;
}

.card-column.organization {
  font-weight: bold;
  font-size: 16px;
  color: #333;
}

.card-column.date,
.card-column.marks {
  text-align: right;
}

.card-column:not(.institution):not(.organization) {
  font-size: 14px;
}

.card-content h4 {
  margin-top: 0;
  font-size: 18px;
  color: #333;
}

.card-details {
  display: flex;
  flex-direction: column;
  gap: 10px;
}

.card-details div {
  font-size: 14px;
  color: #666;
}

.card-details div strong {
  color: #333;
}

