appsmith.onReady(function () {
  const userProfile = appsmith.model.user;
  const education = appsmith.model.education;
  const experience = appsmith.model.workex;
  const profileDetails = document.getElementById('profileDetails');
  function renderProfile(profile) {
    // Fallback image URL
    const fallbackImage = "https://replicate.delivery/yhqm/YP01jTxjgsYgFx2NDZsabAVniYubeSRj4UB0f3RV15AIuwWTA/out-0.webp";

    // Check if profile.image is valid
    const imageUrl = profile.image ? profile.image : fallbackImage;
    let htmlContent = `
            <div class="edit-icon">
                <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="currentColor" class="bi bi-pencil" viewBox="0 0 16 16">
                    <path d="M12.146.854a.5.5 0 0 1 .708 0l2.292 2.292a.5.5 0 0 1 0 .708l-9.292 9.292-3.5 1.5a.5.5 0 0 1-.646-.646l1.5-3.5 9.292-9.292zM11.207 2.5L3 10.707V13h2.293l8.207-8.207-2.293-2.293zM10.5 3.207L12.793 5.5 14 4.293 11.707 2 10.5 3.207z"/>
                </svg>
            </div>
            <div class="profile-item">
                <img src="${imageUrl}">
                <div class="profile-info">
                    <h2>${profile.name}</h2>
                    <p><strong>Role:</strong> ${profile.role}</p>
                    <p><strong>Community ID:</strong> ${profile.community_id}</p>
                    <p><strong>Email:</strong> ${profile.email}</p>
                    <p><strong>Phone:</strong> ${profile.phoneno}</p>
                    <p><strong>Location:</strong> ${profile.location}</p>
                    <p><strong>About:</strong> ${profile.about}</p>
                </div>
            </div>
        `;
    if (education.length > 0) {
      htmlContent += '<h3>Education</h3>';
      education.forEach(edu => {
        htmlContent += `
                    <div class="card">
                        <div class="card-content">
                            <div class="card-row">
                                <div class="card-column institution">${edu.school}</div>
                                <div class="card-column date">${edu.startdate} - ${edu.enddate || 'Present'}</div>
                            </div>
                            <div class="card-row">
                                <div class="card-column">${edu.specialization}</div>
                                <div class="card-column marks">${edu.marks} %/CGPA</div>
                            </div>
                        </div>
                    </div>
                `;
      });
    }
    if (experience.length > 0) {
      htmlContent += '<h3>Work Experience</h3>';
      experience.forEach(exp => {
        htmlContent += `
                    <div class="card">
                        <div class="card-content">
                            <div class="card-row">
                                <div class="card-column organization">${exp.organization}</div>
                                <div class="card-column role">${exp.role}</div>
                                <div class="card-column date">${exp.startdate} - ${exp.enddate || 'Currently Working'}</div>
                            </div>
                            <div class="card-row skills-row">
                                <div class="card-column skills">${exp.skills}</div>
                            </div>
                        </div>
                    </div>
                `;
      });
    }
    profileDetails.innerHTML = htmlContent;
  }
  renderProfile(userProfile);
  const editProfileButton = document.querySelector('.button');
  if (editProfileButton) {
    editProfileButton.addEventListener('click', function () {
      appsmith.triggerEvent("onEditProfileClick");
    });
  }
});

