.widget-container {
  width: auto; /* Allow width to adjust based on content */
  height: auto; /* Allow height to adjust based on content */
  padding: 20px;
  font-family: Arial, sans-serif;
  overflow: hidden; /* Prevent scrollbars from appearing */
}

.header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 20px;
}

h2 {
  margin: 0;
  font-size: 24px;
  color: #333;
}

.search-bar {
  padding: 10px;
  font-size: 16px;
  width: 300px;
  border: 1px solid #ccc;
  border-radius: 4px;
}

.institutions-list {
  display: block; /* Full width for each card */
}

.institution-card {
  display: flex;
  flex-direction: row;
  align-items: flex-start; /* Align items at the top */
  background-color: #fff;
  border-radius: 8px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  padding: 15px;
  cursor: pointer;
  width: calc(100% - 40px); /* Full width minus any padding for margins */
  margin: 0 auto 20px; /* Center the card horizontally with automatic left and right margins */
}

.institution-card:hover {
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
}

.institution-image {
  width: 70px;
  height: auto;
  margin-right: 15px;
}

.institution-info {
  display: flex;
  justify-content: space-between;
  align-items: flex-start; /* Aligns items at the top */
  width: 100%;
}

.institution-details {
  display: flex;
  flex-direction: column;
  width: 70%;
}

.institution-name {
  font-size: 18px;
  font-weight: bold;
  color: #333;
}

.institution-description {
  font-size: 14px;
  color: #555;
  margin-top: 10px;
}

.institution-address {
  font-size: 14px;
  color: #777;
  text-align: right;
  align-self: flex-start; /* Ensures the address aligns at the top */
}

