// Global variable for institutions

let institutions = [];
// Function to create the institution card
function createInstitutionCard(institution) {
  return `
    <div class="institution-card" data-institution-name="${institution.name}">
      <img class="institution-image" src="${institution.image ? institution.image : 'data:image/png;base64,'}" alt="${institution.name}" />
      <div class="institution-info">
        <div class="institution-details">
          <div class="institution-name">${institution.name}</div>
          <div class="institution-description">${institution.description || 'No description available'}</div>
        </div>
        <div class="institution-address">${institution.address || 'Address not provided'}</div>
      </div>
    </div>
  `;
}

// Function to display the filtered institutions
function displayInstitutions(data) {
  const institutionsList = document.getElementById("institutionsList");
  institutionsList.innerHTML = data.map(institution => createInstitutionCard(institution)).join("");
}

// Function to handle card click
function handleCardClick(name) {
  alert(`Selected Institution: ${name}`);
}

// Function to handle search
function handleSearch() {
  const searchTerm = document.getElementById("searchInput").value.toLowerCase();
  const filteredInstitutions = institutions.filter(institution => institution.name.toLowerCase().includes(searchTerm) || institution.address?.toLowerCase().includes(searchTerm) // Include address in the search safely
  );
  displayInstitutions(filteredInstitutions);
}

// Initialization function to ensure everything is ready

appsmith.onReady(() => {
  const fetchedInstitutions = appsmith.model.institutions;
  institutions = fetchedInstitutions; // Store fetched institutions globally
  displayInstitutions(institutions);

  // Add search event listener after the DOM is ready
  const searchInput = document.getElementById("searchInput");
  searchInput.addEventListener("input", handleSearch);

  // Add click event listeners to institution cards
  const institutionCards = document.querySelectorAll('.institution-card');
  institutionCards.forEach(card => {
    card.addEventListener('click', () => handleCardClick(card.dataset.institutionName));
  });
});

